import React,{useState} from "react";
import { createImportEqualsDeclaration } from "typescript";



function App() 

{
const[number1,setNumber1]=useState(0);
const[number2,setNumber2]=useState(0);
const[total,setTotal]=useState(number1+number2);

 let result=0;  
  function num1(event)
  {
  return setNumber1(event.target.value)
  }

  // function num2(event)
  // {
  //   return setNumber2(event.target.value) 
  // }

  function  calculateTotal()
{
result=parseInt(number1)+parseInt(result);
 return setTotal(result);
}

  return(<div className="container">
     <div>
     <input className="num1" type="number" placeholder="Enter a number 1" onChange={num1}></input>
     <br/>
     {/* <br/>
     <input className="num2" type="number" placeholder="Enter a number 2" onChange={num2}></input>
     <br/> */}
     <br/>

     <h1>{total}</h1>

     <button className="onclick" onClick={calculateTotal}>Total</button>
     </div>
   </div>
  )


}

export default App;
