function App() 

{

const [count,setCount]=useState(0);
 console.log(count);



   function increase()
   {
   setCount(count++)

  }

  return(
       <div className="container">
     <div>
     <h1>{count}</h1>
     <button onClick={increase}>+</button>
     </div>
   </div>
  )


}

export default App;
