import React,{useState,useEffect} from 'react';
import Newuser from './Components/Newuser'
import Welcome from './Components/Welcome'
import classes from  './App.module.css'
import {BrowserRouter as Router,Route,Switch,useHistory} from 'react-router-dom';
import SplitPage from './Components/SplitPage';
import Failed from './Components/Failed';




function App() {

    const [mystatus,setStatus]=useState(false);
    const[toggle,setToggle]=useState(true);

    let history=useHistory();

    useEffect(()=>{
      if(window.navigator.onLine===true)
      {
         history.push("/");

      }
      else if(window.navigator.onLine===false){
        history.push("/failed");
      }

    },[window.navigator.onLine])


   const handleToggle=()=>{
    setToggle(!toggle)
   }

   useEffect(()=>{
    document.body.style.backgroundColor=toggle?"black":"White";
   },[toggle])


   const renderDiv=()=>{
     return(<div className={toggle?classes.darkmode:classes.lightmode}>
      <button onClick={handleToggle}>{toggle?"Light Mode":"Dark Mode"}</button>    
     <Router>
       <div className="App">
        <Switch>
          <Route exact path="/">
            <Welcome/>
          </Route>
          <Route path="/login">
          <SplitPage/>
          </Route>
          <Route path="/newuser">
          <Newuser/>
          </Route>
          <Route path="/failed">
            <Failed/>
          </Route>

        </Switch>
      </div>

      </Router>
      </div>)
   }



   return(
  renderDiv()
   )
}

export default App;
