import React,{useState} from "react";
import {useForm} from "react-hook-form";

function Login()
{
    const [email,setEmail]=useState();
    const [password,setPassword]=useState();

    const{register,handleSubmit,formState:{errors}}=useForm()
    // const submit=submission=>console.log(submission);

    function handleEmail(event)
    {   
        event.preventDefault()
       let value1=event.target.value;
        return(setEmail(value1));
    }

    function handlePassword(event)
    {
        event.preventDefault()
        let value2=event.target.value;
        return(setPassword(value2));
    }

    function handleClick(event)
    {
     event.preventDefault()
     let finalEmail=email;   
     let finalPassword=password
    //  handleSubmit(submit);
     return(console.log(finalEmail,finalPassword));
    }
   return(<div>
       <form>
      <h1>Login</h1>
      <input type="text" onChange={handleEmail} placeholder="Enter your email address"/>
      <br/>
      <br/>
      <input type="password" {...register('password',{required:{value:true,message:"Enter a password"},minLength:{value:7,message:"Length should be greater than 7 characters"},})} onChange={handlePassword} placeholder="Enter your password"/>
      <br/>
      {errors.password && (<span>{errors.password.message}</span>)}
      <br/>
      <button type="submit" onClick={handleSubmit(handleClick)} >Login</button>
      </form>
   </div>); 
}

export default Login;
