import React,{useState} from "react";
import {useForm} from "react-hook-form";

function Newuser()
{
    const [email,setEmail]=useState();
    const [password,setPassword]=useState();

    const{register,handleSubmit,formState:{errors}}=useForm()
    // const submit=submission=>console.log(submission);

   const [credentials,updateCredentials]=useState({username:null,password:null});

    function handleEmail(event)
    {   
        event.preventDefault()
       let value1=event.target.value;
        return(setEmail(value1));
    }

    function handlePassword(event)
    {
        event.preventDefault()
        let value2=event.target.value;
        return(setPassword(value2));
    }

    function handleClick(event)
    {
     let finalEmail=email;   
     let finalPassword=password;
    //  return(console.log(finalEmail,finalPassword));

    updateCredentials({
      username:finalEmail,
      password:finalPassword
    })
     if(localStorage.getItem('credentials')===null)
     {
        localStorage.setItem('credentials',JSON.stringify(credentials))
     }
     let old_data=[];
     old_data=JSON.parse(localStorage.getItem('credentials'));
     old_data.push(credentials)
     localStorage.setItem('credentials',JSON.stringify(old_data))
    }
   return(<div>
       <form>
      <h1>Register New User</h1>
      <input type="text" {...register('email',{required:{value:true,message:"Enter a email address"},pattern: {value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,message: "Invalid email address"}})}onChange={handleEmail} placeholder="Enter your email address"/>
      <br/>
      {errors.email && (<span>{errors.email.message}</span>)}
      <br/>
      <br/>
      <input type="password" {...register('password',{required:{value:true,message:"Enter a password"},minLength:{value:8,message:"Length should be of min 8 characters "},validate: (value) => {return (
            [/[a-z]/, /[A-Z]/, /[0-9]/, /[^a-zA-Z0-9]/].every((pattern) =>
              pattern.test(value)
            ) || "Password must include lower, upper, number, and special chars"
          );
        }})} onChange={handlePassword} placeholder="Enter your password"/>
      <br/>
      {errors.password && (<span>{errors.password.message}</span>)}
      <br/>
      <br/>
      <button type="submit" onClick={handleSubmit(handleClick)} >Login</button>
      <br/>
      <br/>
      <input type="number" placeholder="Enter your age"/>
      </form>
   </div>); 
}

export default Newuser;
